package io.jmix.windturbines.view.inspection;

import com.vaadin.flow.component.HasValue;
import com.vaadin.flow.component.datepicker.DatePicker;
import io.jmix.core.DataManager;
import io.jmix.core.Id;
import io.jmix.flowui.ViewNavigators;
import io.jmix.flowui.action.DialogAction;
import io.jmix.flowui.component.accordion.JmixAccordion;
import io.jmix.flowui.component.checkbox.JmixCheckbox;
import io.jmix.flowui.component.radiobuttongroup.JmixRadioButtonGroup;
import io.jmix.flowui.component.select.JmixSelect;
import io.jmix.flowui.component.textfield.TypedTextField;
import io.jmix.flowui.component.validation.ValidationErrors;
import io.jmix.flowui.kit.component.button.JmixButton;
import io.jmix.flowui.testassist.FlowuiTestAssistConfiguration;
import io.jmix.flowui.testassist.UiTest;
import io.jmix.flowui.testassist.UiTestUtils;
import io.jmix.flowui.view.View;
import io.jmix.windturbines.JmixWindturbinesApplication;
import io.jmix.windturbines.entity.*;
import io.jmix.windturbines.entity.inspection.GearboxOilLevelAnswer;
import io.jmix.windturbines.entity.inspection.Inspection;
import io.jmix.windturbines.entity.inspection.RotorBladesAnswer;
import io.jmix.windturbines.entity.inspection.YesNoAnswer;
import io.jmix.windturbines.test_data.EntityTestData;
import io.jmix.windturbines.test_data.entity.ManufacturerData;
import io.jmix.windturbines.test_data.entity.OperatorData;
import io.jmix.windturbines.test_data.entity.ScheduledInspectionData;
import io.jmix.windturbines.test_data.entity.TurbineData;
import io.jmix.windturbines.test_support.DatabaseCleanup;
import io.jmix.windturbines.test_support.TestDialogsConfiguration;
import io.jmix.windturbines.test_support.jmix.TestDialogs;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Nested;
import org.junit.jupiter.api.Test;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.context.SpringBootTest;

import java.time.LocalDate;
import java.time.OffsetDateTime;
import java.time.temporal.ChronoUnit;

import static org.assertj.core.api.Assertions.assertThat;
import static org.assertj.core.api.Assertions.within;


@UiTest
@SpringBootTest(
        properties = {"spring.main.allow-bean-definition-overriding=true"},
        classes = {
                JmixWindturbinesApplication.class,
                FlowuiTestAssistConfiguration.class,
                TestDialogsConfiguration.class
        })
public class InspectionDetailViewTest {

    private final LocalDate MAINTENANCE_TASK_DATE = LocalDate.now();
    @Autowired
    DataManager dataManager;
    @Autowired
    DatabaseCleanup databaseCleanup;
    @Autowired
    EntityTestData entityTestData;
    @Autowired
    ViewNavigators viewNavigators;
    @Autowired
    TestDialogs dialogs;

    private Turbine vestasV150;
    private Manufacturer vestas;
    private Inspection inspection;

    @BeforeEach
    void setUp() {
        databaseCleanup.removeAllEntities();
        vestas = entityTestData.saveWithDefaults(new ManufacturerData("Vestas"));
        Operator skywind = entityTestData.saveWithDefaults(new OperatorData(), it -> {
            it.setName("SkyWind Enterprises");
            it.setContactPerson("John Doe");
            it.setAddress("123 Windy Lane");
            it.setEmail("info@skywind.com");
            it.setPhone("+1 555 1234");
        });
        vestasV150 = entityTestData.saveWithDefaults(new TurbineData(vestas, skywind), it -> {
            it.setTurbineId("#738901");
            it.setModel("V150-4.2 MW");
            it.setStatus(TurbineStatus.OPERATING);
            it.setLocation("Santa Barbara, California");
            it.setHeight(150);
            it.setRotorDiameter(120);
            it.setLastMaintenanceDate(MAINTENANCE_TASK_DATE.minusMonths(1));
            it.setInstallationDate(MAINTENANCE_TASK_DATE.minusYears(3));
        });

        inspection = entityTestData.saveWithDefaults(new ScheduledInspectionData(vestasV150, null), it -> {
            it.setInspectionDate(MAINTENANCE_TASK_DATE);
            it.setTaskStatus(TaskStatus.SCHEDULED);
        });


        dialogs.clear();
    }

    @Test
    void when_openDetail_then_detailViewIsShown() {

        // given
        InspectionDetailView detailView = navigateTo(InspectionDetailView.class, inspection, Inspection.class);

        // expect
        assertThat(detailView.getEditedEntity())
                .isEqualTo(inspection);
    }

    @Test
    void when_openDetail_then_detailsPanelShowsInspectionDetails() {

        // when
        InspectionDetailView detailView = navigateTo(InspectionDetailView.class, inspection, Inspection.class);

        // then
        assertThat(fieldValue(detailView, "turbineField"))
                .isEqualTo(vestasV150);
        assertThat(fieldValue(detailView, "inspectionDateField"))
                .isEqualTo(MAINTENANCE_TASK_DATE);
        assertThat(fieldValue(detailView, "technicianField"))
                .isEqualTo(null);
    }

    private <V extends View<?>, E> V navigateTo(Class<V> viewClass, E entity, Class<E> entityClass) {
        viewNavigators.detailView(UiTestUtils.getCurrentView(), entityClass)
                .withViewClass(viewClass)
                .editEntity(entity)
                .navigate();
        return UiTestUtils.getCurrentView();
    }

    @Nested
    class WizardBehavior {

        @Test
        void when_openDetail_then_prevNextAreShown_and_finishIsNotShown() {
            // when
            InspectionDetailView detailView = navigateTo(InspectionDetailView.class, inspection, Inspection.class);

            // then
            assertThat(prev(detailView).isVisible())
                    .isTrue();
            assertThat(next(detailView).isVisible())
                    .isTrue();

            // and
            assertThat(finish(detailView).isVisible())
                    .isFalse();
        }

        @Test
        void when_openDetail_then_nextIsEnabled_and_prevIsDisabled() {
            // when
            InspectionDetailView detailView = navigateTo(InspectionDetailView.class, inspection, Inspection.class);

            // then
            assertThat(prev(detailView).isEnabled())
                    .isFalse();
            assertThat(next(detailView).isEnabled())
                    .isTrue();
        }

        @Test
        void when_next_then_prevBtnIsEnabled() {
            // given
            InspectionDetailView detailView = navigateTo(InspectionDetailView.class, inspection, Inspection.class);

            // and
            assertThat(currentAccordionIndex(detailView))
                    .isEqualTo(0);

            // when
            next(detailView).click();

            // then
            assertThat(prev(detailView).isEnabled())
                    .isTrue();
        }

        @Test
        void when_next_then_nextPanelIsShown() {
            // given
            InspectionDetailView detailView = navigateTo(InspectionDetailView.class, inspection, Inspection.class);

            // and
            assertThat(currentAccordionIndex(detailView))
                    .isEqualTo(0);

            // when
            next(detailView).click();

            // then
            assertThat(currentAccordionIndex(detailView))
                    .isEqualTo(1);

        }

        @Test
        void when_lastPanel_then_nextIsInvisible_and_finishIsVisible() {
            // given
            InspectionDetailView detailView = navigateTo(InspectionDetailView.class, inspection, Inspection.class);

            // when: to checklist panel
            next(detailView).click();

            // and: to findings panel
            next(detailView).click();

            // and: to recommendations panel
            next(detailView).click();

            // and: to operator signature panel
            next(detailView).click();

            // then
            assertThat(next(detailView).isVisible())
                    .isFalse();

            // and
            assertThat(finish(detailView).isVisible())
                    .isTrue();
        }

        @Test
        void when_finish_then_validationIsPerformed() {
            // given
            InspectionDetailView detailView = navigateTo(InspectionDetailView.class, inspection, Inspection.class);

            // given
            LocalDate newInspectionDate = LocalDate.now().minusDays(1);
            inspectionDateField(detailView).setValue(newInspectionDate);

            // and
            next(detailView).click();
            next(detailView).click();
            next(detailView).click();

            // when
            finish(detailView).click();

            // then: no confirmation dialog is shown, as there are supposed to be validation errors
            assertThat(dialogs.getOptionDialogs())
                    .isEmpty();

            // and
            ValidationErrors validationErrors = UiTestUtils.validateView(detailView);

            // and
            assertThat(validationErrors.getAll())
                    .isNotEmpty();
        }

        @Test
        void when_finish_then_valuesAreSaved_and_statusIsCompleted() {
            // given
            InspectionDetailView detailView = navigateTo(InspectionDetailView.class, inspection, Inspection.class);

            // given
            LocalDate newInspectionDate = LocalDate.now().minusDays(1);
            inspectionDateField(detailView).setValue(newInspectionDate);

            // and
            next(detailView).click();

            rotorBladesAnswerField(detailView).setValue(RotorBladesAnswer.YES);
            gearboxOilLevelAnswerField(detailView).setValue(GearboxOilLevelAnswer.HIGH);
            generatorCheckAnswerField(detailView).setValue(1);
            controlSystemStatusField(detailView).setValue(YesNoAnswer.YES);

            // and
            next(detailView).click();
            next(detailView).click();

            String signatureImage = "data:image/png;base64,";
            detailView.setSignatureImage(signatureImage);

            // and
            operatorRepNameField(detailView).setValue("John Doe");
            operatorConfirmationField(detailView).setValue(true);

            // when
            finish(detailView).click();

            // then
            dialogs.openedOptionDialog().closeWith(DialogAction.Type.YES);

            // and
            Inspection savedInspection = entityTestData.reload(Id.of(inspection));

            assertThat(savedInspection.getTaskStatus())
                    .isEqualTo(TaskStatus.COMPLETED);

            // and
            assertThat(savedInspection.getInspectionDate())
                    .isEqualTo(newInspectionDate);
            assertThat(savedInspection.getOperatorConfirmation())
                    .isTrue();
            assertThat(savedInspection.getOperatorRepSignature())
                    .isNotNull();
            assertThat(savedInspection.getOperatorRepName())
                    .isEqualTo("John Doe");
            assertThat(savedInspection.getOperatorRepSignedAt())
                    .isCloseTo(OffsetDateTime.now(), within(1, ChronoUnit.SECONDS));
        }

        private static JmixButton next(InspectionDetailView detailView) {
            return button(detailView, "nextBtn");
        }
        private static JmixButton prev(InspectionDetailView detailView) {
            return button(detailView, "prevBtn");
        }
        private static JmixButton finish(InspectionDetailView detailView) {
            return button(detailView, "finishBtn");
        }
        private static int currentAccordionIndex(InspectionDetailView detailView) {
            return accordion(detailView).getOpenedIndex().orElse(-1);
        }
    }

    private static JmixAccordion accordion(InspectionDetailView detailView) {
        return UiTestUtils.getComponent(detailView, "mainAccordion");
    }

    private static DatePicker inspectionDateField(InspectionDetailView detailView) {
        return datePicker(detailView, "inspectionDateField");
    }

    private JmixCheckbox operatorConfirmationField(InspectionDetailView detailView) {
        return checkbox(detailView, "operatorConfirmationField");
    }

    private static JmixSelect<RotorBladesAnswer> rotorBladesAnswerField(InspectionDetailView detailView) {
        return select(detailView, "rotorBladesAnswerField");
    }

    private static JmixSelect<GearboxOilLevelAnswer> gearboxOilLevelAnswerField(InspectionDetailView detailView) {
        return select(detailView, "gearboxOilLevelAnswerField");
    }

    private static JmixSelect<YesNoAnswer> controlSystemStatusField(InspectionDetailView detailView) {
        return select(detailView, "controlSystemStatusField");
    }

    private static TypedTextField<String> operatorRepNameField(InspectionDetailView detailView) {
        return stringTextField(detailView,"operatorRepNameField");
    }

    private static JmixRadioButtonGroup<Integer> generatorCheckAnswerField(InspectionDetailView detailView) {
        return radioButtonGroup(detailView, "generatorCheckAnswerField");
    }

    private static Object fieldValue(View<?> view, String componentId) {
        return ((HasValue) UiTestUtils.getComponent(view, componentId)).getValue();
    }

    private static JmixButton button(View<?> view, String componentId) {
        return UiTestUtils.getComponent(view, componentId);
    }

    private static DatePicker datePicker(View<?> view, String componentId) {
        return UiTestUtils.getComponent(view, componentId);
    }

    private static TypedTextField<String> stringTextField(View<?> view, String componentId) {
        return UiTestUtils.getComponent(view, componentId);
    }

    private static JmixCheckbox checkbox(View<?> view, String componentId) {
        return UiTestUtils.getComponent(view, componentId);
    }

    private static <T> JmixSelect<T> select(View<?> view, String componentId) {
        return UiTestUtils.getComponent(view, componentId);
    }

    private static <T> JmixRadioButtonGroup<T> radioButtonGroup(View<?> view, String componentId) {
        return UiTestUtils.getComponent(view, componentId);
    }
}
